//Build Carousel images for banner
class CarouselWithIndicatorDemo extends StatefulWidget {
  @override
  State<StatefulWidget> createState() {
    return _CarouselWithIndicatorState();
  }
}

class _CarouselWithIndicatorState extends State<CarouselWithIndicatorDemo> {
  int _current = 0;

  @override
  Widget build(BuildContext context) {
    return userBannerImages != null && userBannerImages.isNotEmpty
        ? Stack(
            children: [
              Container(
                decoration: BoxDecoration(
                    borderRadius: BorderRadius.all(Radius.circular(12.0))),
                child: Column(children: [
                  CarouselSlider(
                    items: <Widget>[
                      for (int i = 0; i < userBannerImages.length; i++)
                        Container(
                          child: Container(
                            margin: EdgeInsets.all(5.0),
                            child: ClipRRect(
                                borderRadius:
                                    BorderRadius.all(Radius.circular(12.0)),
                                child: Stack(
                                  children: <Widget>[
                                    CachedNetworkImage(
                                        width: 2000.0,
                                        fit: BoxFit.cover,
                                        imageUrl: userBannerImages[i],
                                        placeholder: (context, url) =>
                                            SpinKitWave(
                                                color: Colors.lightBlueAccent),
                                        errorWidget: (context, url, error) {
                                          return CachedNetworkImage(
                                            width: 2000.0,
                                            fit: BoxFit.cover,
                                            imageUrl:
                                                'https://images.unsplash.com/photo-1523205771623-e0faa4d2813d?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=89719a0d55dd05e2deae4120227e6efc&auto=format&fit=crop&w=1953&q=80',
                                            placeholder: (context, url) =>
                                                SpinKitWave(
                                                    color:
                                                        Colors.lightBlueAccent),
                                          );
                                        }),
                                  ],
                                )),
                          ),
                        )
                    ],
                    options: CarouselOptions(
                        autoPlay: true,
                        autoPlayCurve: Curves.easeInOutCubic,
                        enlargeCenterPage: false,
                        viewportFraction: 1.02,
                        aspectRatio: 2.0,
                        onPageChanged: (index, reason) {
                          setState(() {
                            _current = index;
                          });
                        }),
                  ),
                ]),
              ),
              Positioned(
                  bottom: 5.0,
                  left: 50.0,
                  right: 50.0,
                  child: Row(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: userBannerImages.map((url) {
                      int index = userBannerImages.indexOf(url);
                      return Expanded(
                        child: Container(
                          width: 45.0,
                          height: 4.0,
                          margin: EdgeInsets.symmetric(
                              vertical: 10.0, horizontal: 2.0),
                          decoration: BoxDecoration(
                            borderRadius: BorderRadius.circular(10.0),
// shape: BoxShape.circle,
                            color: _current == index
                                ? Colors.white //Color.fromRGBO(0, 0, 0, 0.9)
                                : Color.fromRGBO(0, 0, 0, 0.4),
                          ),
                        ),
                      );
                    }).toList(),
                  )),
            ],
          )
        : Stack(
            children: [
              Container(
                decoration: BoxDecoration(
                    borderRadius: BorderRadius.all(Radius.circular(10))),
                child: Column(children: [
                  CarouselSlider(
                    items: dummyImageSliders,
                    options: CarouselOptions(
                        autoPlay: true,
                        autoPlayCurve: Curves.easeInOutCubic,
                        enlargeCenterPage: false,
                        viewportFraction: 1.02,
                        aspectRatio: 2.0,
                        onPageChanged: (index, reason) {
                          setState(() {
                            _current = index;
                          });
                        }),
                  ),
                ]),
              ),
              Positioned(
                  bottom: 5.0,
                  left: 50.0,
                  right: 50.0,
                  child: Row(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: dummyBannerImages.map((url) {
                      int index = dummyBannerImages.indexOf(url);
                      return Expanded(
                        child: Container(
                          width: 45.0,
                          height: 4.0,
                          margin: EdgeInsets.symmetric(
                              vertical: 10.0, horizontal: 2.0),
                          decoration: BoxDecoration(
                            borderRadius: BorderRadius.circular(10.0),
// shape: BoxShape.circle,
                            color: _current == index
                                ? Colors.white //Color.fromRGBO(0, 0, 0, 0.9)
                                : Color.fromRGBO(0, 0, 0, 0.4),
                          ),
                        ),
                      );
                    }).toList(),
                  )),
            ],
          );
  }
